operation.add:
	if ($this->user->hasPermission('access', 'extension/product_import')){
	$system[] = array(
		'name'	   => 'Product Import Tool',
		'href'     => $this->url->link('extension/product_import', 'user_token=' . $this->session->data['user_token'], true),
		'children' => array()		
	);
}

if ($this->user->hasPermission('access', 'extension/product_export')) {
	$system[] = array(
		'name'	   => 'Product Export Tool',
		'href'     => $this->url->link('extension/product_export', 'user_token=' . $this->session->data['user_token'], true),
		'children' => array()	
	);
}
